mapper.select:
select id, title, head_image, user_id, category_id, status, publish_time, created_time, modified_time
from a_article
where 1 = 1


        and title like concat('%', article.title, '%')


        and category_id = #{article.categoryId}


        and status = #{article.status}


        and user_id = #{article.userId}


        and publish_time >= #{beginPubTime}


        and publish_time <= #{endPubTime}


order by modified_time desc

  limit #{start},#{rows}

select count(1)
from a_article
where 1 = 1


        and title like concat('%', article.title, '%')


        and category_id = #{article.categoryId}


        and status = #{article.status}


        and user_id = #{article.userId}


        and publish_time >= #{beginPubTime}


        and publish_time <= #{endPubTime}
