mapper.select:
select uc.*, uu.username, uu.photo as user_photo
from u_comment uc
left join u_user uu on uc.user_id = uu.id
where 1 = 1

    and uc.parent_id is null or uc.parent_id = ''



        and uc.article_id = ${comment.articleId}


        and uc.created_time <= #{comment.createdTime}


order by uc.created_time desc

    limit #{start},#{rows}

select count(1)
from u_comment uc
where 1 = 1

    and uc.parent_id is null



        and uc.article_id = ${comment.articleId}


        and uc.created_time <= #{comment.createdTime}
