mapper.select:
select * from u_fan
where 1 = 1

    and to_user_id = ${fan.toUserId}


    and from_user_id = ${fan.fromUserId}

order by created_time desc

    limit #{start}, ${rows}

select id, username, photo from u_user
where id in (select from_user_id from u_fan
              where to_user_id = ${id}
              order by created_time desc)

    limit #{start}, ${rows}
